tivecommons.org/licenses/by-sa/3.0/\"\r\n        },\r\n        \"taxonomy\": {\r\n          \"class\": \"Magnoliopsida\",\r\n          \"family\": \"Liliaceae\",\r\n          \"genus\": \"Lilium\",\r\n          \"kingdom\": \"Plantae\",\r\n          \"order\": \"Liliales\",\r\n          \"phylum\": \"Magnoliophyta\"\r\n        },\r\n        \"synonyms\": [\r\n          \"Lirium\",\r\n          \"Martagon\",\r\n          \"Nomocharis\"\r\n        ],\r\n        \"scientific_name\": \"Lilium\",\r\n        \"structured_name\": {\r\n          \"genus\": \"lilium\"\r\n        }\r\n      },\r\n      \"probability\": 0.07790943662460062,\r\n      \"confirmed\": false,\r\n      \"similar_images\": [\r\n        {\r\n          \"id\": \"18602144b9569010ae8c8201fa5bfe91\",\r\n          \"similarity\": 0.2528319628732301,\r\n          \"url\": \"https://plant-id.ams3.cdn.digitaloceanspaces.com/similar_images/images/186/02144b9569010ae8c8201fa5bfe91.jpg\",\r\n          \"url_small\": \"https://plant-id.ams3.cdn.digitaloceanspaces.com/similar_images/images/186/02144b9569010ae8c8201fa5bfe91.small.jpg\"\r\n        },\r\n        {\r\n          \"id\": \"945584f1779c91225c1fd59a53275514\",\r\n          \"similarity\": 0.22592089474807847,\r\n          \"url\": \"https://plant-id.ams3.cdn.digitaloceanspaces.com/similar_images/images/945/584f1779c91225c1fd59a53275514.jpg\",\r\n          \"url_small\": \"https://plant-id.ams3.cdn.digitaloceanspaces.com/similar_images/images/945/584f1779c91225c1fd59a53275514.small.jpg\"\r\n        }\r\n      ]\r\n    },\r\n    {\r\n      \"id\": 234343026,\r\n      \"plant_name\": \"Aloe vera\",\r\n      \"plant_details\": {\r\n        \"common_names\": [\r\n          \"Barbados aloe\",\r\n          \"burn plant\",\r\n          \"coastal aloe\",\r\n          \"common aloe\",\r\n          \"Curau00e7ao aloe\",\r\n          \"Indian aloe\",\r\n          \"Jaffarabad aloe\",\r\n          \"medicinal aloe\",\r\n          \"Mediterranean aloe\",\r\n          \